import { Component } from '@angular/core';
import { ToastrService } from 'ngx-toastr';
import { Employee } from 'src/app/shared/employee.model';
import { EmployeeService } from 'src/app/shared/employee.service';

@Component({
  selector: 'app-employee-form',
  templateUrl: './employee-form.component.html',
  styleUrls: ['./employee-form.component.css']
})
export class EmployeeFormComponent {
  submitted = false;

  constructor(public service: EmployeeService, private toastr: ToastrService) { }

  onSubmit() {
    this.submitted = true;
    if (this.service.employeeForm.valid) {
      console.log(this.service.employeeForm.value);

      //lets post the data
      this.service.postEmployee().subscribe(res => {
        console.log('got the response !')
        this.resetForm();
        this.service.getEmployee();
      })
    }
  }

  resetForm() {
    this.service.employeeForm.reset({
      _id: null,
      fullName: '',
      position: '',
      location: '',
      salary: ''
    });
    this.submitted = false;
  }

}
<form class="row" [formGroup]="service.employeeForm"
(submit)="onSubmit()" [class.submitted]="submitted">
    <div class="col-12 mb-3">
        <label for="" class="form-label">Full Name</label>
        <input type="text" class="form-control form-control-lg" placeholder="Enter your full name"
            formControlName="fullName">
    </div>
    <div class="col-12 mb-3">
        <label for="" class="form-label">Location</label>
        <input type="text" class="form-control form-control-lg" placeholder="Enter the location"
            formControlName="location">
    </div>
    <div class="col-6 mb-3">
        <label for="" class="form-label">Position</label>
        <input type="text" class="form-control form-control-lg" placeholder="Any position"
            formControlName="position">
    </div>
    <div class="col-6 mb-3">
        <label for="" class="form-label">Salary</label>
        <input type="number" class="form-control form-control-lg" placeholder="L.P.A"
            formControlName="salary">
    </div>
    <div class="col-12 d-grid">
        <button class="btn btn-lg btn-primary" type="submit">SUBMIT</button>
    </div>
</form>
